package evaluacion1;

public class Triafifa {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n , f , f2 ,  col;
		
		n =5;
		f2=n;
		
			for (f=1; f <= f2; f++) {
				for (col=1; col<=f; col++){
					System.out.print(f);
					System.out.print("\t");
				}
				
				System.out.print("\n");
			}
			
	}	

}
