package evaluacion1;

public class Triafiwd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

int n , f , columna;
		
		n=5;
		
		
		f = n;
		
		
		while (f>=1) {
			columna=1;
			while (columna<=f) {
				System.out.print(f);
				System.out.print("\t");
				
				columna++;
			}
			
			System.out.print("\n");
			f--;
			
			
			
		}

	}

}
